const operate = require("./index")

//Declaring 2 variables for values x and y
var x = 5
var y = 2

console.log(operate.add(x,y)) // Should return 7
console.log(operate.subtract(x,y)) // Should return 3
console.log(operate.multiply(x,y)) // Should return 10
console.log(operate.divide(x,y)) // Should return 2.5
console.log(operate.modulation(x,y)) // Should return 1
console.log(operate.elevate(x,y)) // Should return 25
console.log(operate.sqrt(x)) // Should return 2.23606797749979
console.log(operate.pi) // Returns 3.141592653589793
console.log(operate.e) // Returns 2.718281828459045
